Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1744','0','Qué contiene','Recoge informaci&oacute;n sobre todas las jornadas que ha desarrollado esta asociaci&oacute;n desde su constituci&oacute;n. En dichas jornadas se han desarrollado temas como: la crisis del estado de bienestar y la Uni&oacute;n Europea, la fragilidad financiera del capitalismo , globalizaci&oacute;n, regulaci&oacute;n p&uacute;blica y desigualdades, alternativas al capitalismo, etc.<br />Adem&aacute;s podemos acceder a varias ponencias y art&iacute;culos de expertos en la Revista de Econom&iacute;a Cr&iacute;tica que publica la Asociaci&oacute;n.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1745','0','Qué es','Asociaci&oacute;n destinada a la creaci&oacute;n de un discurso cr&iacute;tico y alternativo con la realidad social, cultura y econ&oacute;mica actual y con el discurso neoliberal que prevalece en todos los aspectos del sistema y que abocan a una serie de desigualdades de todo tipo y a modelos insostenibles desde una perspectiva ecol&oacute;gica y social.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1823','0','Qué contiene','- P&aacute;gina de opini&oacute;n: temas en torno a la ley sobre cultura de paz o di&aacute;logo, alianza de civilizaciones, etc.<br />- Programa anual de actividades: ciclos de cine, conferencias, jornadas, encontros, campa&ntilde;as de sensibilizaci&oacute;n, experiencias, cursos, etc.<br />- Informaci&oacute;n, artigos e material diverso sobre conflitividade e convivencia escolar.<br />- Publicaci&oacute;ns e recursos: gu&iacute;as did&aacute;cticas<br />- Videoteca ecopacifista en v&iacute;deo e dvd coa s&uacute;a correspondente Unidade Did&aacute;ctica co obxecto de traballar na aula os valores c&iacute;vicos e democr&aacute;ticos, a convivencia, os conflictos e a s&uacute;a resoluci&oacute;n pac&iacute;fica, os dereitos humanos, a interculturalidade, a discriminaci&oacute;n, contidos curriculares, transversais...<br />- Bibliograf&iacute;a sobre cultura de paz<br />- Documental: &quot;En Son de Paz&quot;<br />- Otras secciones: Cooperaci&oacute;n o Desenvolvemento, Documentos, Premios e Reco&ntilde;ecementos, Arquivo de noticias, etc.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1824','0','Qué es ','O Seminario Galego de Educaci&oacute;n para a Paz naceu en 1985 como un Seminario Permanente promovido por un grupo de profesionais dos distintos niveis do ensino (desde a educaci&oacute;n infantil ata a universidade) sensibilizados diante da ausencia de materiais did&aacute;cticos e reflexi&oacute;ns te&oacute;ricas sobre a Educaci&oacute;n para a Paz en xeral ou especificamente sobre os dereitos humanos, a tolerancia, a solidariedade, o ecopacifismo, a soluci&oacute;n pac&iacute;fica de conflictos, a interculturalidade ou a Cultura de Paz.&nbsp;<br /><br />Nos &uacute;ltimos anos profesionais doutros &aacute;mbitos (dereito, sa&uacute;de, animaci&oacute;n sociocultural, estudiantes...) t&eacute;&ntilde;ense incorporado &oacute; Seminario Galego de Educaci&oacute;n para a Paz que, na actualidade, tense convertido nunha ONG sen &aacute;nimo de lucro que forma parte da Coordinadora Galega de ONG&acute;s para o Desenvolvemento e da Coordinadora Galega pola Paz.',null);
