Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1829','0','Qué contiene','Not&iacute;cies d''''actualitat, activitats (concurs d''''animacions per la pau, exposici&oacute; itinerant, pon&egrave;ncies, curs, trobadas...), campanyes de sensibilitzaci&oacute; social i pressi&oacute; pol&iacute;tica, publicacions editades no gratuits, arxiu documental per temes i per &agrave;mbit geogr&agrave;fic, i din&aacute;miques i jocs aptes per treballar l''''Educaci&oacute; per la Pau. <br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1830','0','Qué es','La Fundaci&oacute; per la Pau es una ONG que t&eacute; per finalitat la instauraci&oacute; progressiva d''''una Cultura de Pau. La Fundaci&oacute;, fruit de la collaboraci&oacute; de molts ciutadans i cutadanes, treballa per afavorir el creixement d''''una consci&egrave;ncia c&iacute;vica, l&uacute;dica i ben informada que pressioni democr&agrave;ticament a favor de la pau.',null);
