Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2208','0','Qué es','<font size="2"><p>Examina y eval&uacute;a fuentes primarias y secundarias de informaci&oacute;n, incluyendo documentos t&eacute;cnicos y comerciales. Asimismo, recibe documentos originados en reuniones, congresos y conferencias. El material es proporcionado por las distintas &aacute;reas del Ministerio de Educaci&oacute;n y por otros organismos p&uacute;blicos y/o privados nacionales e internacionales.</p></font>',null);
