Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2217','0','Qué contiene','Relaci&oacute;n de cursos sobre cine y educaci&oacute;n impartidos durante el a&ntilde;o 2005.<br />Por secciones contiene:<br />-&nbsp;Aula M&egrave;dia: Editoriales, prensa, radio, televisi&oacute;n, materiales audivisuales, revistas.<br />-&nbsp;CinEscola: este espacio se compone de una filmoteca y de fichas t&eacute;cnicas y materiales did&aacute;cticos sobre las pel&iacute;culas.<br />-&nbsp;Educom: jornadas de educci&oacute;n y comunicaci&oacute;n local<br />-&nbsp;Servicio de formaci&oacute;n, jornadas y talleres impartidos por Aulamedia.<br />-&nbsp;Enlaces de inter&eacute;s con otras p&aacute;ginas y organizaciones relacionadas con temas educativos.',null);
