Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1494','17',null,'<span lang="ES" style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana; mso-ansi-language: ES; mso-no-proof: yes"><p style="BACKGROUND: white; MARGIN-BOTTOM: 12pt; TEXT-ALIGN: justify"><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">Pentru aceasta, CREADE trebuie s&#259; reuneasc&#259; alte instan&#355;e &icirc;n afara propriilor administra&#355;ii &#351;i institu&#355;ii educative. &Icirc;n definitiv, trebuie s&#259; reuneasc&#259; toate acele instan&#355;e care, de fapt, sunt deja reunite &icirc;n jurul unui proiect comun: integrarea imigran&#355;ilor, coeziunea social&#259; &#351;i educarea cet&#259;&#355;eneasc&#259; a societ&#259;&#355;ii &icirc;n ansamblu. Este absolut necesar s&#259; lucr&#259;m &icirc;mpreun&#259; &icirc;n acest proiect, &icirc;n toate mediile care constituie&nbsp;structura de baz&#259; a&nbsp;societ&#259;&#355;ii,&nbsp;adop&acirc;nd o atitudine integral&#259; &#351;i multidisciplinar&#259;.&nbsp;</span><br /><br /><br /></p></span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1958','0','p_01','<strong>Decreto 78/2004 de 2 de abril</strong> (DOG 23 de abril de 2004), por el que se crea y regula el <em>Observatorio Galego da Inmigraci&oacute;n e da Loita contra o racismo e a xenofobia</em>.<br /><br /><a href="http://www.galiciaaberta.com/portal/binary/com.epicentric.contentmanagement.servlet.ContentDeliveryServlet/Decretos/Decreto_78-2004_del_2_de_Abril_por_el_que_se_crea_y_regula_el_Observatorio%2528Castellano%2529.pdf" target="_new">Decreto 78/2004 de 2 de abril (castellano)</a><br /><br /><a href="http://www.galiciaaberta.com/portal/binary/com.epicentric.contentmanagement.servlet.ContentDeliveryServlet/Decretos/Decreto_78-2004_do_2_de_Abril_polo_que_se_crea_e_regula_o_Observatorio%2528Galego%2529.pdf" target="_new">Decreto 78/2004 de 2 de abril (gallego)</a><br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1493','17',null,'<span lang="ES" style="FONT-SIZE: 8.5pt; FONT-FAMILY: Verdana; mso-ansi-language: ES; mso-no-proof: yes"><span lang="RO" style="mso-ansi-language: RO"><font size="3"><font face="Times New Roman"><p><span lang="RO" style="mso-ansi-language: RO"><p style="BACKGROUND: white; TEXT-ALIGN: justify"><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">Nu se poate lucra &icirc;n acest proiect &icirc;n mod izolat. Sistemul educativ &#351;i politica social&#259; nu pot func&#355;iona separat.<p><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">Activit&#259;&#355;ile care trebuie desf&#259;&#351;urate &icirc;n fiecare dintre domeniile implicate sunt de natur&#259; diferit&#259;, dar este timpul <em>de a ne coordona eforturile, de a face schimb de informa&#355;ii, de a difuza practicile bune &#351;i de a genera re&#355;ele de lucru &icirc;n comun </em>plec&acirc;nd de la administra&#355;iile educative, de la agen&#355;ii sociali, &#351;i de la toate sectoarele implicate &icirc;n <em>construirea unei cet&#259;&#355;enii interculturale</em>, care trebuie s&#259; fie, a&#351;a cum am mai semnalat, obiectivul nostru ultim. </span></p></span></p></span></p><p>&nbsp;</p></font></font></span></span>',null);
