Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2004','0','Qué contiene','Se organiza en cuatro secciones:<br />- Biblioteca, con una extensa bibliograf&iacute;a de obras juveniles, informaci&oacute;n sobre autores e ilustradores y listado de pel&iacute;culas basadas en obras de literatura infantil y juvenil.<br />- Taller, un espacio para alojar cuentos e ilustraciones&nbsp;enviados por los lectores.<br />- Agenda, con enlaces a editoriales, revistas e instituciones especializadas.<br />- Espacio informativo sobre el Centro, su historia y actividades.',null);
