Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2156','0','Qué es','La Revista Electr&oacute;nica de Investigaci&oacute;n Educativa (REDIE) es una publicaci&oacute;n del Instituto de Investigaci&oacute;n y Desarrollo Educativo de la Universidad Aut&oacute;noma de Baja California, cuyo objetivo principal es dar a conocer la investigaci&oacute;n que se realiza a nivel regional, nacional e internacional en el campo de la educaci&oacute;n.',null);
