Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2159','0','Qué contiene','Art&iacute;culos de investigaci&oacute;n y evaluaci&oacute;n educativa centrados en aspectos concretos. Bajo el ep&iacute;grafe &quot;diversidad&quot; se incluyen documentos relacionados con educaci&oacute;n e interculturalidad.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2160','0','Qué es','RELIEVE es la primera revista electr&oacute;nica de Espa&ntilde;a, tiene una orientaci&oacute;n acad&eacute;mica, establece un proceso de revisi&oacute;n por expertos y es totalmente gratuita. Aspira a ser un instrumento de comunicaci&oacute;n para la comunidad educativa, en particular en temas de investigaci&oacute;n y de evaluaci&oacute;n educativa, entendidas en un sentido amplio. RELIEVE est&aacute; auspiciada por AIDIPE (Asociaci&oacute;n Interuniversitaria de Investigaci&oacute;n en Pedagog&iacute;a) y se edita actualmente desde el Departamento de M&eacute;todos de Investigaci&oacute;n y Diagn&oacute;stico en Educaci&oacute;n (MIDE) de la Facultad de Filosof&iacute;a y Ciencias de la Educaci&oacute;n de la Universidad de Valencia.',null);
