Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2163','0','Qué contiene','Textos completos de art&iacute;culos sobre investigaciones de car&aacute;cter emp&iacute;rico elaboradas desde cualquier enfoque metodol&oacute;gico y disciplina (educaci&oacute;n, psicolog&iacute;a, sociolog&iacute;a, econom&iacute;a, historia, etc.), revisiones de la literatura (especialmente meta-an&aacute;lisis), experiencias en centros escolares, experiencias y aplicaciones en el campo de pol&iacute;ticas educativas y reflexiones te&oacute;ricas. REICE publica tambi&eacute;n recensiones de libros y documentos, as&iacute; como comentarios sobre los diferentes art&iacute;culos publicados.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2164','0','Qué es','Publicaci&oacute;n oficial de la Red Iberoamericana de Investigaci&oacute;n sobre Cambio y Eficacia Escolar (RINACE). REICE pretende convertirse en un foro de encuentro, debate y difusi&oacute;n de la investigaci&oacute;n sobre Calidad, Equidad, Eficacia y Cambio en Educaci&oacute;n realizada en Iberoam&eacute;rica y por investigadores Iberoamericanos. REICE tiene una periodicidad semestral, por lo que cada volumen est&aacute; conformado por dos n&uacute;meros. Dichos n&uacute;meros aparecen a principios de julio y a comienzos de enero.',null);
