Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1990','0','p_01','<p>Autora: Carme Barba Corominas<br />&nbsp;<br />Una WebQuest per a tots els educadors interessats en treballar els aspectes de la interculturalitat, la llengua i la cohesi&oacute; social a l&rsquo;escola.<br />L&rsquo;objectiu d&rsquo;aquesta WebQuest &eacute;s donar a con&egrave;ixer els recursos educatius que hi ha a Internet que ens poden ajudar per a treballar la llengua, la interculturalitat&nbsp; i la cohesi&oacute; social. <br /></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2120','0','Qué es','Es la revista electr&oacute;nica internacional, de libre acceso, del &quot;Teachers College Comparative and International Education Program&quot; de la Universidad de Columbia. Surgi&oacute; en 1997 y est&aacute; planteada como una plataforma de debate sobre la educaci&oacute;n actual en el mundo.',null);
