Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2124','0','Qué es','Revista, con versi&oacute;n electr&oacute;nica e impresa, publicada por la &quot;Association of American Colleges and Universities&quot;, con el objetivo de ayudar a institutos y universidades a entender la diversidad como un compromiso institucional y una prioridad educativa.',null);
