Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1935','0','p_02','<strong>Resoluci&oacute;n de 25 de enero de 2005</strong>, de la Direcci&oacute;n General de Pol&iacute;tica Educativa, por la que se autoriza el programa experimental de acogida y de integraci&oacute;n de alumnos inmigrantes y se dictan instrucciones para su desarrollo.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1936','0','p_01','<strong>Orden de 25 de junio de 2001</strong> (BOA 6/07/01), por la que se establecen medidas de intervenci&oacute;n educativa dirigidas, entre otros, a alumnos inmigrantes y entre cuyos objetivos se encuentran la adquisici&oacute;n de las competencias comunicativas y ling&uuml;&iacute;sticas b&aacute;sicas en la lengua de acogida y la potenciaci&oacute;n de las distintas culturas.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1937','0','p_04','<strong>Resoluci&oacute;n de 10 de mayo de 2002</strong> (B.O.C. de 20 de mayo), de la Direcci&oacute;n General de Ordenaci&oacute;n e Innovaci&oacute;n Educativa, por la que se dictan instrucciones para la regulaci&oacute;n de materias optativas de car&aacute;cter complementario y para la elaboraci&oacute;n, aprobaci&oacute;n y desarrollo del Plan de Atenci&oacute;n a la Diversidad.',null);
