Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1506','17',null,'<p style="BACKGROUND: white; MARGIN-BOTTOM: 12pt; TEXT-ALIGN: justify"><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">&middot;&nbsp;S&#259; ofere centrelor &#351;i echipelor de speciali&#351;ti servicii de asisten&#355;&#259; &#351;i formare &icirc;n scopul dezvolt&#259;rii competen&#355;elor interculturale.<p>&nbsp;<span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">&middot;&nbsp;S&#259; promoveze re&#355;ele de colaborare &#351;i spa&#355;ii de &icirc;nt&acirc;lnire pentru reflec&#355;ii, schimburi de idei &#351;i propuneri.<p>&nbsp;<span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">&middot;&nbsp;S&#259; faciliteze ob&#355;inerea de informa&#355;ii despre sistemele educative din &#355;&#259;rile de origine ale elevilor no&#351;tri, sub aspect curricular, organiza&#355;ional &#351;i metodologic.<p>&nbsp;</p></span></p></span></p></span></p><p>&nbsp;</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('851','11',null,'Atrav&eacute;s desta sec&ccedil;&atilde;o vamos aproximar-nos &agrave;s caracter&iacute;sticas b&aacute;sicas de outros sistemas educativos, especialmente daqueles dos quais procede o nosso grupo de alunos, com a inten&ccedil;&atilde;o de conhecer melhor o contexto educativo dos seus pa&iacute;ses de origem. Tamb&eacute;m&nbsp;incluiremos informa&ccedil;&atilde;o &uacute;til relativa &agrave;s homologa&ccedil;&otilde;es, tanto do ensino universit&aacute;rio como n&atilde;o universit&aacute;rio: conv&eacute;nios em vigor, como solicit&aacute;-las, onde se dirigir...',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1122','11',null,'Este espa&ccedil;o da p&aacute;gina facilita links ut&eacute;is para obter informa&ccedil;&atilde;o relevante acerca dos movimentos migrat&oacute;rios, da evolu&ccedil;&atilde;o da popula&ccedil;&atilde;o, da diversidade particular do grupo de alunos que hoje em dia habitam as nossas escolas, etc.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2518','112',null,'O cami&ntilde;o cara a esa nova cidadan&iacute;a pasa pola progresiva adopci&oacute;n dun enfoque intercultural. A educaci&oacute;n intercultural def&iacute;nese ao mesmo&nbsp;tempo como un proceso de cambio e unha meta a alcanzar. O CREADE nace como unha aposta valente e comprometida que pretende acompa&ntilde;ar e orientar este proceso cara ao seu fin.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1495','112',null,'Entre as s&uacute;as principais intenci&oacute;ns est&aacute; a <em>de reflexionar con profundidade sobre o modelo de escola e de sociedade que queremos, </em>e ir articulando unha transformaci&oacute;n social e educativa que deixe de p&oacute;r o foco nalg&uacute;ns alumnos para ir, paulatinamente, dirix&iacute;ndoo cara a todos... Porque unha sociedade m&aacute;is xusta, m&aacute;is igualitaria, m&aacute;is inclu&iacute;nte e intercultural &eacute;, sen d&uacute;bida, algo colectivo. Tr&aacute;tase da construci&oacute;n dunha nova cidadan&iacute;a na que todos e cada un merecemos ser protagonistas.<br />&nbsp;&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1494','112',null,'Por iso, o CREADE ha de reunir&nbsp;a m&aacute;is instancias&nbsp;c&aacute;s propias Administraci&oacute;ns e instituci&oacute;ns educativas. En definitiva, a todas aquelas que de facto xa est&aacute;n aglutinadas en torno&nbsp;a un proxecto com&uacute;n: a integraci&oacute;n dos inmigrantes, a cohesi&oacute;n social e a educaci&oacute;n cidad&aacute; do conxunto da sociedade.&nbsp;&Eacute; absoluta a necesidade de traballar conxuntamente en iso desde todos os &aacute;mbitos que vertebran a sociedade, cun <em>enfoque integral e multidisciplinar.</em>&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1493','112',null,'Non podemos traballar nesta empresa de maneira illada. O sistema educativo por si s&oacute; non pode, e as pol&iacute;ticas sociais sen o sistema educativo tampouco.<br /><br />As actuaci&oacute;ns que desde cada un dos escenarios implicados han de porse en marcha son de moi diferente natureza, pero &eacute; hora&nbsp;de <em>coordinar esforzos, de compartir informaci&oacute;n, de difundir boas pr&aacute;cticas e de xerar redes de traballo conxunto</em> desde as administraci&oacute;ns educativas, os axentes sociais, e todos os sectores implicados na <em>construci&oacute;n dunha cidadan&iacute;a intercultural,</em> que ha de ser, como sinalamos, o &uacute;ltimo e gran obxectivo.',null);
