Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2175','0','Qué contiene','La versi&oacute;n digital de la Revista Iberoamericana de Educaci&oacute;n permite el acceso a los contenidos de su versi&oacute;n impresa, en su idioma original, de forma gratuita. Adem&aacute;s, dispone de diversas secciones dise&ntilde;adas para la participaci&oacute;n y el intercambio de ideas y experiencias entre sus lectores.',null);
