Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2180','0','Qué es','A global knowledge base, intended to share the results of professional activities in order to foster intercultural education. The base reflect the broad profile of the UNESCO Chairs programme related to key themes of intercultural education, including culture of peace, human rights, intercultural dialogue, international understanding, tolerance, civic education and constructive resolution of conflicts.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2183','0','Qué contiene','The ERIC online system provides the public with a centralized ERIC Web site for searching the ERIC bibliographic database of more than 1.1 million citations going back to 1966.<br /><br />Base de datos en la que se puede realizar b&uacute;squedas sencillas y avanzadas mediante varios descriptores y a trav&eacute;s de un tesauro alfab&eacute;tico de tem&aacute;tica muy variada. <br /><br /><br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2184','0','Qué es','The Education Resources Information Center (ERIC), sponsored by the Institute of Education Sciences (IES) of the U.S. Department of Education, produces the world&rsquo;s premier database of journal and non-journal education literature.',null);
