Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1465','0','Qué contiene','<p>Informaci&oacute;n sobre legislaci&oacute;n, congresos y reuniones, cursos y seminarios, novedades bibliogr&aacute;ficas, notas de prensa y proyectos (revista y centro de documentaci&oacute;n). Tambi&eacute;n recoge links a centros de investigaci&oacute;n y generadores de estad&iacute;sticas y a revistas cient&iacute;ficas digitales, en su mayor&iacute;a consultables gratuitamente y otras por suscripci&oacute;n.</p>',null);
