Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2268','0','Qué contiene','<p>Adem&aacute;s de fundamentos te&oacute;ricos en relaci&oacute;n con los distintos tipos y &aacute;mbitos de la mediaci&oacute;n, puede encontrarse informaci&oacute;n sobre bibliograf&iacute;a, recursos y otros enlaces, as&iacute; como un directorio de mediadores en distintos pa&iacute;ses del mundo (con la posibilidad de a&ntilde;adir informaci&oacute;n sobre profesionales). Contiene secciones espec&iacute;ficas dedicadas a la mediaci&oacute;n escolar y comunitaria, as&iacute; como diversos foros. Ofrece formaci&oacute;n on-line.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2269','0','Qué es','Inter-mediaci&oacute;n es una compa&ntilde;&iacute;a internacional, con sede en Fort Lauderdale (Florida, USA.),&nbsp;fundada en 1996 con el prop&oacute;sito de ofrecer servicios tanto a nivel individual como organizacional en los campos de educaci&oacute;n, consultor&iacute;a, investigaci&oacute;n y publicaciones en resoluci&oacute;n de conflictos. La oferta de estos servicios se basa en un enfoque sist&eacute;mico, profundamente respetuoso de las diferencias cross-culturales, y de la dignidad de las partes en disputa.',null);
