Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2303','0',null,'- Formaci&oacute;n y asesoramiento a centros y equipos de trabajo.<br /><br />- Edici&oacute;n de materiales y recursos de inter&eacute;s para el &aacute;mbito educativo, social y cultural.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1507','112',null,'<p>&middot;&nbsp;Proporcionar recursos interculturais que respondan &aacute;s demandas dos e das profesionais do &aacute;mbito social e educativo.<br /><br />&middot;&nbsp;Obter, sistematizar e xerar todo&nbsp;tipo de informaci&oacute;n relacionada coa educaci&oacute;n intercultural.<br /><br />&middot;&nbsp;Desenvolver novos materiais e ferramentas naquelas &aacute;reas e tem&aacute;ticas nas que se perciban necesidades.<br /><br />&middot;&nbsp;Fomentar innovaci&oacute;ns e investigaci&oacute;ns no &aacute;mbito da atenci&oacute;n educativa &aacute; diversidade cultural.<br /><br /><br />&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1506','112',null,'&middot;&nbsp;Ofrecer a centros e equipos de profesionais asesoramento e formaci&oacute;n para o desenvolvemento de competencias interculturais.<br /><br />&middot;&nbsp;Promover redes de colaboraci&oacute;n e espazos de encontro para a reflexi&oacute;n, o intercambio e a formulaci&oacute;n de propostas.<br /><br />&middot;&nbsp;Facilitar informaci&oacute;n sobre os sistemas educativos dos pa&iacute;ses de orixe do noso alumnado, nos&nbsp;seus aspectos curriculares, organizativos e metodol&oacute;xicos.<br />&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1120','112',null,'Un aspecto relevante do noso mundo &eacute; a confluencia e a necesaria interconexi&oacute;n entre persoas con referentes culturais diversos. Quizais sempre foi as&iacute;, pero no noso aqu&iacute; e agora esta confluencia ten caracter&iacute;sticas e urxencias concretas. Desde&nbsp;o &aacute;mbito educativo &eacute; preciso traballar <em>partindo dun concepto de educaci&oacute;n amplo e plural,</em> cun <em>enfoque comunitario do feito educativo, </em>orientado cara ao desenvolvemento dunha nova cidadan&iacute;a, desexable e necesariamente intercultural.<br />&nbsp;&nbsp;',null);
