Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2187','0','Qué contiene','Se incluyen los trabajos publicados en revistas, congresos y libros cuya tem&aacute;tica sea la Psicolog&iacute;a y otras disciplinas afines, editadas en Espa&ntilde;a y Am&eacute;rica Latina, desde 1975 hasta la actualidad. El acceso gratuito a trav&eacute;s de internet permite realizar b&uacute;squedas por palabra clave, temas, autor y publicaci&oacute;n.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2188','0','Qué es','Base de datos bibliogr&aacute;fica, con enlaces a los textos completos, especializada en Psicolog&iacute;a y disciplinas afines. El acceso gratuito permite realizar b&uacute;squedas bibliogr&aacute;ficas en la base de datos PSICODOC pero restringe la visualizaci&oacute;n a tres registros. Se necesita realizar la suscripci&oacute;n para tener acceso completo a la base de datos. <br />',null);
