Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2195','0','Qué contiene','Cubre la documentaci&oacute;n de libros, documentos, art&iacute;culos de publicaciones seriadas, conferencias, seminarios, investigaciones, etc. publicadas por la CEDOC, como de la sede, oficinas regionales, subregionales y nacionales, como tambi&eacute;n de publicaciones externas realizadas por ONGs de educaci&oacute;n, ministerios de educaci&oacute;n, universidades, etc. <br /><br /><br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2196','0','Qué es','Base de datos del Centro de Documentación e Información (CEDOC), de la sede de la Unesco en Par&iacute;s, de las oficinas regionales y nacionales, como tambi&eacute;n de centros especializados, ministerios de educaci&oacute;n y universidades.<br /><br />',null);
