Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2224','0','Qué es','Curso formativo que pertenece al Programa Ram&oacute;n y Cajal de la Consejer&iacute;a de Educaci&oacute;n del Gobierno de Arag&oacute;n. El programa Ram&oacute;n y Cajal tiene por objetivo integrar el uso innovador de las tecnolog&iacute;as, de modo que profesores y alumnos las utilicen como una herramienta m&aacute;s en los procesos de ense&ntilde;anza-aprendizaje.',null);
