Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2227','0','Qué es','Es un programa de apoyo a la educaci&oacute;n intercultural creado por la Fundaci&oacute; Jaume Bofill en el que participan personas y entidades que trabajan en los &aacute;mbitos de la educaci&oacute;n y la inmigraci&oacute;n. La iniciativa nace de la voluntad de promover el an&aacute;lisis, la reflexi&oacute;n y la acci&oacute;n respecto a la convivencia intercultural.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2228','0','Idiomas','Espa&ntilde;ol',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2229','0','Qué contiene','La informaci&oacute;n se encuentra distribuida por secciones:<br />- Cooperaci&oacute;n, con el siguiente contenido:Proyectos de desarrollo,Campa&ntilde;as de Ayuda humanitaria y Comercio justo.<br />- Campa&ntilde;as, propuestas de acci&oacute;n y educaci&oacute;n: entre otras, destacamos la Campa&ntilde;a Mundial por la Educaci&oacute;n.<br />- Publicaciones: Libros, Estudios, Boletines, Materiales pedag&oacute;gicos y Notas de prensa.<br />- Informaci&oacute;n de contacto',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2230','0','Qué es','ONG destinada a la cooperaci&oacute;n internacional con el objetivo de paliar las desigualdades sociales producto del sistema y que persigue ayudar a los colectivos&nbsp; m&aacute;s desfavorecidos a conseguir; el derecho a&nbsp;&nbsp; unos medios de vida sostenibles, el derecho a la salud y la educaci&oacute;n, el derecho a la vida&nbsp; y a la seguridad, el derecho a la propia identidad y el derecho a fomentar una cultura solidaria.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2231','0','Idiomas','Castellano',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2232','0','Qué contiene','Adem&aacute;s de Publicaciones del Seminario de Educaci&oacute;n para la Paz hay un link a una p&aacute;gina de recursos did&aacute;cticos acerca de educaci&oacute;n intercultural, educaci&oacute;n en valores, educaci&oacute;n para el desarrollo y educaci&oacute;n para la paz.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2233','0','Qué es','El Seminario de Educaci&oacute;n para la Paz de la Asociaci&oacute;n Pro Derechos Humanos (Sedupaz), se constituy&oacute; en 1987. Re&uacute;ne a personas procedentes de distintos niveles de la educaci&oacute;n formal y no formal, que entienden&nbsp; la educaci&oacute;n para la paz como una educaci&oacute;n liberadora, orientada a alentar la acci&oacute;n social en pro de la paz y la justicia. Una educaci&oacute;n encaminada a formar personas capaces de convertirse en obst&aacute;culos a la pr&aacute;ctica generalizada de imbuir -mediante los procesos de socializaci&oacute;n- valores y contenidos que fomentan el conformismo, el androcentrismo, la intolerancia, el etnocentrismo y dem&aacute;s actitudes que contribuyen a perpetuar la violencia estructural y directa en el planeta.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2234','0','Idiomas','Castellano',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2250','0','Qué contiene','<p>Consta de cuatro bases de datos:<br />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Redined-Investigaci&oacute;n, que recoge tesis doctorales, tesinas, investigaciones e informes en materia educativa desde el a&ntilde;o 1975.<br />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Redined-Innovaci&oacute;n, centrada en proyectos de este tipo desde el a&ntilde;o 1980.<br />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Redined-Recursos, con proyectos desde 1980.<br />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Redined-Revistas, con una base de datos de revistas de &aacute;mbito espa&ntilde;ol desde 1999.<br />Los campos a los que se puede acceder son los siguientes:<br />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T&iacute;tulo, autores, fecha y localizaci&oacute;n del documento.<br />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Objetivos, m&eacute;todos, modelos, variables, instrumentos utilizados, t&eacute;cnicas de an&aacute;lisis, etc.<br />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Descriptores tem&aacute;ticos, metodol&oacute;gicos y nacionales.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2251','0','Qué es','<p>REDINED es una red de bases de datos descentralizada, constituida por unidades en cada una de las comunidades aut&oacute;nomas y, a la vez, interconectadas para posibilitar el intercambio de informaci&oacute;n entre las diferentes instituciones dedicadas a la investigaci&oacute;n e innovaci&oacute;n en materias incluidas en la categor&iacute;a de Ciencias de la Educaci&oacute;n.<br /></p>',null);
