Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2253','0','Idiomas','<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span style="FONT-SIZE: 11pt; FONT-FAMILY: Arial; mso-bidi-font-size: 12.0pt">Ingl&eacute;s, y una peque&ntilde;a parte de la base de datos se ofrece tambi&eacute;n en espa&ntilde;ol.<p>&nbsp;</p></span></p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2254','0','Qué contiene','<span style="FONT-SIZE: 11pt; FONT-FAMILY: Arial; mso-fareast-font-family: ''''Times New Roman''''; mso-bidi-font-size: 12.0pt; mso-ansi-language: ES; mso-fareast-language: ES; mso-bidi-language: AR-SA">La b&uacute;squeda en la base de datos se puede realizar por tipo de campo (30 temas) y por continente.<span style="mso-spacerun: yes">&nbsp; </span>Cada campo ofrece acceso r&aacute;pido a documentos, organizaciones, temas de investigaci&oacute;n, etc.</span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2255','0','Qué es','<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span style="FONT-SIZE: 11pt; FONT-FAMILY: Arial; mso-bidi-font-size: 12.0pt">Eldis es uno de los recursos del Institute of Development Studies, Sussex. Ofrece una base de datos con m&aacute;s de 18000 documentos completos de descarga gratuita relacionados con temas de diversa &iacute;ndole (educaci&oacute;n, infancia y juventud, g&eacute;nero, conflictos, biodiversidad, salud, globalizaci&oacute;n, etc.).</span></p>',null);
