Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2087','0','P_05','<p>-&nbsp;Sistemas educativos: esta secci&oacute;n, sin ser propiamente una base de datos, ofrece informaci&oacute;n sobre los distintos sistemas educativos en el mundo y sobre pol&iacute;ticas educativas y sociales comparadas (documentos oficiales, estudios comparados, homologaciones, etc.). <br />-&nbsp;Ense&ntilde;anza L2. Proporciona informaci&oacute;n, materiales y recursos relacionados con la ense&ntilde;anza de lenguas no maternas.<br /><br /></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2088','0','P_04','<p>-&nbsp; Legislaci&oacute;n: Recopila el conjunto de normativas existentes en relaci&oacute;n con la diversidad cultural y la educaci&oacute;n en sentido amplio. Se ordenan en funci&oacute;n de los distintos &aacute;mbitos internacional, europeo, estado espa&ntilde;ol y comunidades aut&oacute;nomas. En esta secci&oacute;n se ofrecen adem&aacute;s apartados espec&iacute;ficos relacionados con temas de especial relevancia, como el de menores no acompa&ntilde;ados o la organizaci&oacute;n de la participaci&oacute;n ciudadana.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2089','0','P_03','<p>-&nbsp;TIC: proporciona recursos de audio, imagen, audiovisuales e interactivos, relacionados con la atenci&oacute;n a la diversidad. Ofrece adem&aacute;s&nbsp; una herramienta inform&aacute;tica que permite elaborar nuevos materiales en formato TIC (Tecnolog&iacute;as de Informaci&oacute;n y Comunicaci&oacute;n).</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2090','0','P_02','<p>-&nbsp;Recursos: recoge todo tipo de documentos bibliogr&aacute;ficos relacionados con la atenci&oacute;n a la diversidad cultural y educaci&oacute;n en general: actas y ponencias, tesis, investigaciones, art&iacute;culos, materiales did&aacute;cticos, experiencias, cuentos y&nbsp; monograf&iacute;as.<br />-&nbsp;En RED: organiza y sistematiza tem&aacute;ticamente los recursos que se ofrecen en Internet.</p>',null);
