Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2203','0','Qué contiene','<p>El Centro de Documentaci&oacute;n en Interculturalidad (CdDI), ubicado en el Edificio Aulario de la Facultad de Ciencias de la Educaci&oacute;n de la Universidad de Granada, cuenta con uno de los fondos documentales m&aacute;s importantes del Estado espa&ntilde;ol sobre migraciones, exclusi&oacute;n social y cultural, racismo, xenofobia, minor&iacute;as &eacute;tnicas, interculturalidad, etc. Adem&aacute;s cuenta con servicio de hemeroteca, con un Observatorio de Medios de Inmigraci&oacute;n y varios enlaces sobre intercultrualidad.</p>',null);
