Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2204','0','Qué es','<p>El objetivo central del CdDI es seleccionar, adquirir, procesar, almacenar y divulgar materiales y recursos documentales y de informaci&oacute;n, relacionados con la interculturalidad, el multiculturalismo y las migraciones, mediante la oferta y promoci&oacute;n de productos y servicios de informaci&oacute;n pertinentes y oportunos, a los miembros de las instituciones acad&eacute;micas, a otros organismos p&uacute;blicos y privados y a los investigadores y especialistas en general. </p>',null);
