Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2288','0',null,'<p>Se enmarca en el nuevo Pla de Ciutadania i Immigraci&oacute; 2005 &ndash; 2008 para promocionar y consolidar la lengua propia en un proyecto educativo pluriling&uuml;e dentro de un modelo educativo intercultural, con una finalidad de cohesi&oacute;n social.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2289','0',null,'<p>Aprobado por el Departament d&rsquo;Ensenyament en abril de 2003, pretende integrar todas las actuaciones realizadas en los centros docentes en relaci&oacute;n a los nuevos alumnos. Insta a los centros educativos a instaurar medidas que garanticen una acogida adecuada, sobretodo en lo que hace referencia a una r&aacute;pida integraci&oacute;n ling&uuml;&iacute;stica que les permita comunicarse y acceder a los contenidos escolares. Este Plan se enmarca en el Pla Interdepertamental d&rsquo;immigraci&oacute; 2001 &ndash; 2004 y recoge las sugerencias y propuestas de la Confer&egrave;ncia Nacional d&rsquo;Educaci&oacute; 2000 &ndash; 2002 en la Secci&oacute;n III, d&rsquo;Atenci&oacute; a la Diversitat. </p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2290','0',null,'<p>El Pla Interdepartamental d&rsquo;Immigraci&oacute;&nbsp;supuso la creaci&oacute;n y desarrollo del Pla Comarcal d&rsquo;Integraci&oacute; dels Immigrants en 24 comarcas catalanas desde 1993. Dichos planes han facilitado la puesta en marcha de algunas experiencias dirigidas a la poblaci&oacute;n inmigrante.</p>',null);
