Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1983','89',null,'<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span lang="IT" style="FONT-SIZE: 9pt; FONT-FAMILY: Arial">L''insegnamento delle seconde lingue sta acquisendo sempre pi&ugrave; importanza nel panorama della diversit&agrave; culturale e linguistica. Nell''ambito educativo, la conoscenza della lingua veicolare della regione in cui si vive, &egrave; fondamentale per la socializzazione. Questa sezione fornisce tutto il materiale necessario per l''insegnamento-apprendimento di lingue non materne: metodologia, proposte didattiche, attivit&agrave; d''interazione e di autoapprendimento, ecc.</span></p>',null);
