Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1127','11',null,'Para al&eacute;m de oferecer uma lista de links, pretendemos configurar um mapa de redes que integre todas as institui&ccedil;&otilde;es, organiza&ccedil;&otilde;es e grupos que trabalham sobre a diversidade na Internet, oferecendo recursos, materiais, informa&ccedil;&atilde;o e/ou forma&ccedil;&atilde;o. Pretende ser como um ponto de encontro, aberto e em constru&ccedil;&atilde;o, para ir sempre acrescentando iniciativas e propostas que nos ajudem a criar um espa&ccedil;o virtual sobre a interculturalidade.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1131','11',null,'<p>A informa&ccedil;&atilde;o foi estruturada em onze sub-sec&ccedil;&otilde;es:<br /><br />- Interculturalidade e educa&ccedil;&atilde;o<br />- Migra&ccedil;&otilde;es<br />- Paz, coopera&ccedil;&atilde;o e desenvolvimento<br />- Democracia e participa&ccedil;&atilde;o<br />- Direitos humanos<br />- G&eacute;nero e diversidade<br />- Media&ccedil;&atilde;o<br />- Informa&ccedil;&atilde;o solid&aacute;ria e alternativa<br />- Institui&ccedil;&otilde;es e organiza&ccedil;&otilde;es nacionais e internacionais<br />- Fontes de informa&ccedil;&atilde;o em educa&ccedil;&atilde;o<br />- Contos e narrativa oral</p><p>Se a sua organiza&ccedil;&atilde;o oferece conte&uacute;dos na Internet similares aos referidos e n&atilde;o foi inclu&iacute;da no nosso mapa, por favor contacte-nos.</p>',null);
