Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('824','112',null,'- O fomento da investigaci&oacute;n e a contribuci&oacute;n &aacute; Rede Eurydice (Rede Europea de Informaci&oacute;n sobre Educaci&oacute;n) a trav&eacute;s da unidade espa&ntilde;ola da dita Rede.<br />- A innovaci&oacute;n, experimentaci&oacute;n e desenvolvemento curricular das ensinanzas, as&iacute; como a elaboraci&oacute;n e difusi&oacute;n de materiais curriculares e outros documentos de apoio que faciliten ao profesorado o desempe&ntilde;o das s&uacute;as funci&oacute;ns.<br />- A organizaci&oacute;n e funcionamento da biblioteca e arquivo do Departamento en materia educativa.<br />&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('803','112',null,'Ofrece informaci&oacute;n sobre as actividades que desenvolve o Centro nas s&uacute;as catro &aacute;reas de competencia, Investigaci&oacute;n, Innovaci&oacute;n, Biblioteca Xeral de Educaci&oacute;n e Arquivo Central de Educaci&oacute;n, as&iacute; como sobre os proxectos e programas internacionais nos que participa (CIDREE, EURYCIDE, GLOBE e REEPS). Facilita o acceso &aacute;s bases de datos sostidas polo MEC: REDINET, EURYBASE e o Cat&aacute;logo de Investigaci&oacute;ns.&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('825','112',null,'Na secci&oacute;n &quot;Infocide&quot; poden atoparse convocatorias de bolsas, premios e concursos, as&iacute; como as &uacute;ltimas publicaci&oacute;ns do Centro a texto completo. Ofrece tam&eacute;n, na secci&oacute;n de &quot;Novidades&quot;, o texto completo das publicaci&oacute;ns de Eurydice (en castel&aacute;n e ingl&eacute;s), e do &uacute;ltimo Bolet&iacute;n editados polo CIDE.',null);
