Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1972','112',null,'<strong>Lei 9/1999 do 18 de novembro,</strong> de Solidaridade na Educaci&oacute;n (BOJA n&ordm; 2 de decembro de 1999), pola que se regula un conxunto de actuaci&oacute;ns para compensar as situaci&oacute;ns de desigualdade que padecen determinados colectivos, entre os que se atopa o alumnado en situaci&oacute;n de desvantaxe sociocultural e o que pertence a minor&iacute;as &eacute;tnicas ou culturais en situaci&oacute;n desfavorable.',null);
