Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('648','183',null,'Ouvrons une grande fen&ecirc;tre et jetons un regard &agrave; l&rsquo;ext&eacute;rieur... Comment sont les syst&egrave;mes d&rsquo;&eacute;ducation d&rsquo;autres pays, comment fonctionnent-ils, dans quels processus de r&eacute;forme sont-ils impliqu&eacute;s ?<br /><br />Les diff&eacute;rences de structure et de contenus par rapport au syst&egrave;me d&rsquo;&eacute;ducation espagnol sont-elles r&eacute;elles ou imaginaires ? Justifient-elles des mesures sp&eacute;ciales pour nos nouveaux &eacute;l&egrave;ves ?<p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>',null);
