Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1952','112',null,'<strong>Instruci&oacute;n conxunta, de marzo do 2005,</strong> das Direcci&oacute;ns Xerais de Ordenaci&oacute;n, Renovaci&oacute;n e Centros de Formaci&oacute;n Profesional e Promoci&oacute;n Educativa relativa a determinados aspectos do proceso de admisi&oacute;n de alumnado con necesidades educativas derivadas de d&eacute;ficits sociais e culturais e de necesidades educativas espec&iacute;ficas.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1962','112',null,'<strong>Resoluci&oacute;n do 31 de maio do 2004,</strong> da Direcci&oacute;n Xeral de Promoci&oacute;n Educativa, pola que se modifica parcialmente a Resoluci&oacute;n do 4 de setembro do 2000 e establ&eacute;cense prazos e determinados aspectos do procedimento para a admisi&oacute;n do alumnado nas aulas de compensaci&oacute;n educativa da Comunidade de Madrid.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1967','112',null,'<strong>Resoluci&oacute;n do 23 de xu&ntilde;o do 2004,</strong> da Conseller&iacute;a de Educaci&oacute;n e Ciencia, pola que se autoriza un gasto de 25.000 euros con destino &aacute; convocatoria p&uacute;blica de subvenci&oacute;ns a asociaci&oacute;ns, organizaci&oacute;ns non gubernamentais e instituci&oacute;ns privadas, sen fins de lucro, para a realizaci&oacute;n de actuaci&oacute;ns de compensaci&oacute;n educativa para o curso 2004-2005, e&nbsp;apr&oacute;banse as bases reguladoras.&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1969','112',null,'<strong>Orde do 12 de marzo do 2002,</strong> pola que se regula o proceso de escolarizaci&oacute;n de alumnos estranxeiros con necesidades educativas derivadas de situaci&oacute;ns sociais ou culturais desfavorecidas nos centros sostidos con fondos p&uacute;blicos.<br /><br />Orde do 12 de marzo do 2002',null);
