Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('86','112',null,'A constituci&oacute;n da <em>Rep&uacute;blica Federal de Alema&ntilde;a</em> reco&ntilde;ece a todos o dereito a desenvolver libremente a s&uacute;a personalidade e a elixir libremente un centro de aprendizaxe, unha profesi&oacute;n e un posto de traballo.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('852','112',null,'A pol&iacute;tica educativa persigue a finalidade de posibilita a todos os cidad&aacute;ns unha promoci&oacute;n &oacute;ptima e unha formaci&oacute;n cualificada que responda aos seus intereses e capacidades e ofrecer aos/as mozos/as unha educaci&oacute;n que os converta en cidad&aacute;ns/&aacute;s cabais, dispostos/as a asumir a s&uacute;a responsabilidade na democracia.&nbsp;&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('811','112',null,'Entre os principios da educaci&oacute;n alemana at&oacute;panse: liberdade de c&aacute;tedra, a educaci&oacute;n temper&aacute;, a educaci&oacute;n permanente, a calidade do sistema educativo (para o que require a implicaci&oacute;n de todos os axentes implicados na educaci&oacute;n).',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('856','112',null,'Segundo a Lei Fundamental (Grundgesetz), os <em>L&auml;nder</em> ou estados federados asumen primordialmente a responsabilidade da administraci&oacute;n e a lexislaci&oacute;n educativa, nun sistema que comprende os Ministerios de Educaci&oacute;n, Cultura e Ciencia dos <em>L&auml;nder</em>, as autoridades rexionais (<em>Bezirksregierung/Oberschulamt</em>) e as autoridades de supervisi&oacute;n escolar de menor rango (<em>Schulamt</em>). <br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1121','183',null,'<p class="MsoNormal" style="MARGIN: 6pt 0cm; LINE-HEIGHT: 130%; TEXT-ALIGN: justify"><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; LINE-HEIGHT: 130%; FONT-FAMILY: Arial">Organismes et &eacute;tablissements &agrave; caract&egrave;re international, autres organismes au niveau national, instituts de statistique et services d&rsquo;&eacute;ducation de chaque r&eacute;gion autonome, voici quelques liens qui sont fournis. De plus, chacun d&rsquo;eux est accompagn&eacute; d&rsquo;un circuit destin&eacute; &agrave; aider et &agrave; orienter la recherche.<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1122','183',null,'<p class="MsoNormal" style="MARGIN: 6pt 0cm; TEXT-ALIGN: justify"><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial">Cet espace du portail&nbsp;fournit des liens utiles pour obtenir de l&rsquo;information importante sur les mouvements migratoires, l&rsquo;&eacute;volution de la population et la diversit&eacute; particuli&egrave;re des &eacute;l&egrave;ves qui &agrave; l&rsquo;heure actuelle peuplent nos &eacute;coles, etc.<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('822','183',null,'<p style="BACKGROUND: white; TEXT-ALIGN: justify"><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial">b)&nbsp;information sur le statut l&eacute;gal des &eacute;trangers (l&eacute;gislation en vigueur, formulaires et r&eacute;f&eacute;rences d&rsquo;&eacute;tablissements et d&rsquo;organismes charg&eacute;s de l&rsquo;instruction des dossiers).<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2266','183',null,'<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify"><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial">c) th&egrave;mes sp&eacute;cifiques utiles, tels que la r&eacute;glementation relative aux mineurs non accompagn&eacute;s dans notre pays.<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('9','183',null,'<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify"><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial">Cette section pr&eacute;tend &ecirc;tre une banque d&rsquo;information et de ressources utiles pour inclure les nouvelles technologies &agrave; la pratique quotidienne.<p>&nbsp;</p></span></p><p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify"><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial">Gr&acirc;ce &agrave; ses r&eacute;f&eacute;rences et ces liens int&eacute;ressants, CREADETIC propose des ressources et des mat&eacute;riels interactifs pour travailler dans des espaces &eacute;ducatifs officiels et non officiels, des outils visant &agrave; d&eacute;velopper nos propres applications et une vaste s&eacute;lection d&rsquo;audiovisuels (images <span style="mso-spacerun: yes">&nbsp;</span>fixes et en mouvement, documentaires, enregistrements, etc.).<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2258','183',null,'<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify"><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial">Il est possible d&rsquo;utiliser sur n&rsquo;importe quel support et sans autorisation pr&eacute;alable toutes les ressources ici disponibles, aussi bien celles &eacute;labor&eacute;es par le CREADE que celles cit&eacute;es en r&eacute;f&eacute;rence, &agrave; la seule condition de citer la source.<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1127','183',null,'<span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: FR; mso-fareast-language: ES; mso-bidi-language: AR-SA">Non seulement nous avons l&rsquo;intention de proposer un listage de liens, mais aussi la configuration de cartes de r&eacute;seaux r&eacute;unissant toutes les institutions, organisations et groupes travaillant sur la diversit&eacute; sur <span style="mso-spacerun: yes">&nbsp;</span>Internet, en proposant des ressources, des mat&eacute;riels, de l&rsquo;information et/ou de la formation. Cette carte est con&ccedil;ue comme un point de rencontre, ouvert et en construction permanente auquel il faudra ajouter des initiatives et des propositions qui nous aideront &agrave; tisser un espace virtuel sur l&rsquo;interculturalit&eacute;.</span>',null);
