Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1936','112',null,'<strong>Orde do 25 de xu&ntilde;o do 2001</strong> (BOA 6/07/01), pola que se establecen medidas de intervenci&oacute;n educativa dirixidas, entre outros, a alumnos inmigrantes e entre os seus obxectivos at&oacute;panse a adquisici&oacute;n das competencias comunicativas e ling&uuml;&iacute;sticas b&aacute;sicas na lingua de acollida e a potenciaci&oacute;n das distintas culturas.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1935','112',null,'<strong>Resoluci&oacute;n do 25 de xaneiro do 2005,</strong> da Direcci&oacute;n Xeral de Pol&iacute;tica Educativa, pola que se autoriza o programa&nbsp;experimental de acollida e de integraci&oacute;n de alumnos inmigrantes e se dictan instruci&oacute;ns para o seu desenvolvemento.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1946','112',null,'<strong>Decreto 228/2000&nbsp;do 26 de xu&ntilde;o,</strong> no que se recollen as funci&oacute;ns da Comisi&oacute;n intercultural de inmigraci&oacute;n, entre elas as campa&ntilde;as de informaci&oacute;n a inmigrantes, a escolarizaci&oacute;n de alumnos inmigrantes e a educaci&oacute;n ling&uuml;&iacute;stica.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1953','112',null,'<strong>Orde do 9 de febreiro do 2005</strong> pola que se regula o proceso de escolarizaci&oacute;n do alumnado de educaci&oacute;n infantil, primaria e secundaria, en centros sostidos con fondos p&uacute;blicos para o curso escolar 2005-2006.&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1958','112',null,'<strong>Decreto 78/2004 do 2 de abril</strong> (DOG 23 de abril do 2004), polo que se crea e regula o <em>Observatorio Galego da Inmigraci&oacute;n e da Loita contra o racismo e a xenofobia.<br /><br /></em>Decreto 78/2004 do 2 de abril (castel&aacute;n)<br /><br />Decreto 78/2004 do 2 de abril (galego)&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1956','112',null,'<strong>Orde do 20 de febreiro do 2004</strong> (DOG 26 de febreiro do 2004), pola que se establecen as medidas de atenci&oacute;n espec&iacute;fica ao alumnado procedente do estranxeiro.<br />(S&oacute; hai acceso ao sumario do DOG, pero non&nbsp;&aacute; disposici&oacute;n completa)&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1960','112',null,'<strong>Orde do 14 de xu&ntilde;o do 2002,</strong> pola que se regula a elaboraci&oacute;n e aplicaci&oacute;n de proxectos de intervenci&oacute;n educativa e do programa de acollida ling&uuml;&iacute;stica e cultural que afectan aos alumnos de incorporaci&oacute;n tard&iacute;a aos Institutos de Educaci&oacute;n Secundaria.<br />',null);
