Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1964','112',null,'<strong>Orde 2316/1999 do 15 de outubro</strong> (BOCM 25 de outubro), pola que se regula o funcionamento das actuaci&oacute;ns de compensaci&oacute;n educativa.<br /><br />Orde 2316/1999 do 15 de outubro&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1963','112',null,'<strong>Resoluci&oacute;n do 4 de setembro do 2000,</strong> da Direcci&oacute;n Xeral de Promoci&oacute;n Educativa, pola que se dictan instruci&oacute;ns para a organizaci&oacute;n das actuaci&oacute;ns de compensaci&oacute;n educativa da ESO nos centros sostidos con fondos p&uacute;blicos (BOCM 12 setembro 2000).<br /><br />Resoluci&oacute;n do 4 de setembro do 2000<br />&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1965','112',null,'<strong>Orde do 7 de maio do 2002,</strong> da Conselleira de Educaci&oacute;n, Univesidades e Investigaci&oacute;n, de modificaci&oacute;n da Orde que regula a acci&oacute;n educativa para o alumnado que se atope en situaci&oacute;ns sociais ou culturais desfavorecidas e as medidas de intervenci&oacute;n educativa para o alumnado que manifeste dificultades graves de adaptaci&oacute;n escolar.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1968','112',null,'<strong>Decreto 69/2002 do 23 de maio,</strong> polo que se ordena e define o curr&iacute;culo de Educaci&oacute;n Secundaria Obrigatoria e onde se sinalan medidas para a atenci&oacute;n ao alumnado en situaci&oacute;n de desvantaxe social.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('851','112',null,'A trav&eacute;s desta secci&oacute;n achegar&eacute;monos &aacute;s caracter&iacute;sticas b&aacute;sicas doutros sistemas educativos, especialmente daqueles dos que procede o noso alumnado, coa intenci&oacute;n de co&ntilde;ecer mellor o contexto educativo dos seus pa&iacute;ses de orixe. Tam&eacute;n incluiremos informaci&oacute;n &uacute;til relativa &aacute;s homologaci&oacute;ns, tanto de ensinanzas universitarias coma non universitarias: convenios en vigor, como solicitalas, a onde dirixirse...',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('87','112','Lexislación_01','<em>A Lei Fundamental (Grundgegetz) </em>estrutura a educaci&oacute;n , dictaminando que esta se atopa baixo supervisi&oacute;n do Estado e cunha duraci&oacute;n m&iacute;nima de escolarizaci&oacute;n de doce anos.<br /><br /><em>A Lei Federal de Fomento da Educaci&oacute;n (BAf&ouml;G)</em> proporciona subvenci&oacute;ns e axudas para a escolarizaci&oacute;n do alumnado.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('855','112',null,'As autoridades encargadas da supervisi&oacute;n educativas de cada <em>Land</em> son responsables da inspecci&oacute;n e a pr&aacute;ctica acad&eacute;mica, as&iacute; como da supervisi&oacute;n do cumplimento da lei e do profesorado dentro do sistema educativo. <br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('853','112',null,'Os Ministerios dos <em>L&auml;nder</em> determinan o curr&iacute;culo, recomendan a metodolox&iacute;a e aproban os libros de texto.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1131','183',null,'<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial">L&rsquo;information a &eacute;t&eacute; structur&eacute;e en&nbsp;onze sous-sections :<br /><br />-&nbsp;interculturalit&eacute; et &eacute;ducation, <br />-&nbsp;migrations,<br />-&nbsp;paix, coop&eacute;ration et d&eacute;veloppement, <br />-&nbsp;d&eacute;mocratie et participation, <br />-&nbsp;droits de l&rsquo;homme, <br />-&nbsp;genre et diversit&eacute;,<br /></span><span lang="FR" style="FONT-SIZE: 13pt; COLOR: black; FONT-FAMILY: Arial; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: FR; mso-fareast-language: ES; mso-bidi-language: AR-SA">- m&eacute;diation,<br />-&nbsp;information solidaire et alternative, <br />-&nbsp;institutions et organisations nationales et internationales,<br />-&nbsp;sources d&rsquo;information en &eacute;ducation,<br />- contes et narrations orales.<br /><br /><p style="BACKGROUND: white; TEXT-ALIGN: justify"><span lang="FR" style="FONT-SIZE: 13pt; FONT-FAMILY: Arial">Si ton organisation propose des contenus sur la toile semblables &agrave; ceux d&eacute;crits et si elle n&rsquo;a pas &eacute;t&eacute; incluse dans notre carte, mets-toi en contact avec nous.<p>&nbsp;</p></span></p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('9','17',null,'<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify"><span lang="ES" style="FONT-SIZE: 8.5pt; COLOR: black; FONT-FAMILY: Verdana">Aceast&#259; sec&#355;iune se dore&#351;te a fi o banc&#259; de resurse &#351;i informa&#355;ii utile al c&#259;rei scop este &icirc;ncorporarea noilor tehnologii &icirc;n practica cotidian&#259;. Pe lang&#259; referin&#355;e &#351;i link-uri interesante, CREADETIC ofer&#259; resurse &#351;i materiale interactive pentru activit&#259;&#355;i &icirc;n spa&#355;ii educative formale &#351;i non-formale, instrumente pentru dezvoltarea propriilor noastre aplica&#355;ii &#351;i o selec&#355;ie foarte ampl&#259; de materiale audiovizuale (imagini fixe &#351;i animate, documentare, &icirc;nregistr&#259;ri, etc.).<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1127','17',null,'<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify"><span lang="ES" style="FONT-SIZE: 8.5pt; COLOR: black; FONT-FAMILY: Verdana">Ceea ce v&#259; punem la dispozi&#355;ie nu este o simpl&#259; list&#259; de link-uri, ci, sper&#259;m noi, o hart&#259; a re&#355;elelor care s&#259; con&#355;in&#259; toate institu&#355;iile, organiza&#355;iile &#351;i grupurile prezente pe Internet &#351;i care desf&#259;&#351;oar&#259; activit&#259;&#355;i legate de diversitate. Propunem un punct de &icirc;nt&acirc;lnire, deschis &#351;i permanent &icirc;n construc&#355;ie, la care putem ad&#259;uga ini&#355;iative &#351;i propuneri care s&#259; ne ajute s&#259; &#355;esem un spa&#355;iu virtual pe tema interculturalit&#259;&#355;ii.<p>&nbsp;</p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('823','17',null,'a) Recomand&#259;ri &#351;i acte normative (recomand&#259;ri, informa&#355;ii, avize &#351;i principii emise de c&#259;tre instan&#355;e interna&#355;ionale, europene, na&#355;ionale &#351;i autonome).',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('648','17',null,'<p style="TEXT-ALIGN: justify"><span lang="ES" style="FONT-SIZE: 7pt; COLOR: black; FONT-FAMILY: Verdana; mso-bidi-font-weight: bold">Haide&#355;i s&#259; deschidem larg fereastra &#351;i s&#259; arunc&#259;m o privire &icirc;n afar&#259;&hellip; Cum sunt, cum func&#355;ioneaz&#259;, &icirc;n ce procese de reform&#259; sunt implicate sistemele educative ale altor &#355;&#259;ri?<br /></span><span lang="ES" style="FONT-WEIGHT: normal; FONT-SIZE: 7pt; COLOR: black; FONT-FAMILY: Verdana; mso-bidi-font-weight: bold"><br />Diferen&#355;ele de structur&#259; &#351;i con&#355;inut &icirc;n ce prive&#351;te sistemul educativ spaniol sunt reale sau imaginare? Trebuie s&#259; adopt&#259;m m&#259;suri speciale pentru noii no&#351;tri elevi?<h3 style="BACKGROUND: white; MARGIN: auto 0cm; TEXT-ALIGN: justify"><p>&nbsp;</p></h3></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('851','17',null,'<h3 style="BACKGROUND: white; MARGIN: auto 0cm; TEXT-ALIGN: justify"><span lang="ES" style="FONT-WEIGHT: normal; FONT-SIZE: 7pt; COLOR: black; FONT-FAMILY: Verdana; mso-bidi-font-weight: bold">&Icirc;n aceast&#259; sec&#355;iune vom privi &icirc;ndeaproape caracteristicile de baz&#259; ale altor sisteme educative &#351;i &icirc;n mod special ale acelor sisteme care au fost frecventate de c&#259;tre elevii no&#351;tri cu scopul de a cunoa&#351;te mai bine contextul educativ al &#355;&#259;rilor lor de origine. De asemenea, vom include informa&#355;ii utile referitoare la echivalarea studiilor la nivel de &icirc;nv&#259;&#355;&#259;m&acirc;nt universitar &#351;i non-universitar: conven&#355;ii &icirc;n vigoare, cum le pute&#355;i solicita, unde v&#259; pute&#355;i adresa&hellip;<p>&nbsp;</p></span></h3>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1983','17',null,'<span lang="ES" style="FONT-SIZE: 7pt; COLOR: black; FONT-FAMILY: Verdana"><p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span lang="ES" style="FONT-SIZE: 7pt; COLOR: black; FONT-FAMILY: Verdana">Predarea limbilor str&#259;ine dob&acirc;nde&#351;te o importan&#355;&#259; din ce in ce mai mare, &icirc;n contextul diversit&#259;&#355;ii culturale &#351;i lingvistice. &Icirc;n mediul educa&#355;ional cunoa&#351;terea limbii vorbite &icirc;n regiunea de reziden&#355;&#259; este fundamental&#259; pentru integrarea social&#259;. Aceast&#259; sec&#355;iune ofer&#259; o gam&#259; larg&#259; de materiale pentru procesul de predare-&icirc;nv&#259;&#355;are a limbilor str&#259;ine: metodologii, propuneri didactice, activit&#259;&#355;i interactive &#351;i de studiu individual, etc.<p>&nbsp;</p></span></p><p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span lang="ES" style="FONT-SIZE: 7pt; COLOR: black; FONT-FAMILY: Verdana"><p>&nbsp;</p></span></p><p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify">&nbsp;</p></span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('648','1',null,'We are opening up a huge window and taking a look outside at the educational systems of other countries... what are they like, how do they work, and what processes of reform are they currently involved in?<br /><br />Are differences in structure and content real or imaginary when compared to the Spanish system? Do they justify implementing special measures for our new students?',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1983','1',null,'Teaching a second language is becoming increasingly important in a world of cultural and linguistic diversity. In the educational system, knowing the vehicular language of the region in which you live is fundamental for socialisation. This section offers all kinds of material for teaching and learning non-native languages: didactic suggestions, interactive activities, teach-yourself tools, etc.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('9','1',null,'This section aims to provide a bank of useful information and resources for incorporating new technologies into everyday practice.<br /><br />As well as useful links and references, CREADETIC offers interactive resources and materials for working in both formal and non-formal educational environments, tools for developing your own software, and a wide selection of audiovisual material (fixed and moving images, documentaries, recordings. etc.).',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2258','1',null,'All the resources available here, whether created by CREADE or the other institutions mentioned, can be used in any support and do not require prior authorisation, the only requirement being to quote the source.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2530','0','Qué contiene','Est&aacute;n iniciando la puesta en internet, de forma progresiva de dicho conjunto de documentos. Mantienen una base de datos, sobre los contenidos informativos en la prensa, directamente consultable en la p&aacute;gina web.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2304','182',null,'<br />Neben der Erfassung und Systematisierung vorhandener  Information und Ressourcen &uuml;ber interkulturelle Erziehung sowie der Pflege und Aktualisierung des Portals beabsichtigt CREADE die Entwicklung weiterer wichtiger Aktionslinien:<br /><br />- Unterst&uuml;tzung der Forschungslinie &uuml;ber interkulturelle Erziehung durch Ausschreibung von Preisen und F&ouml;rderung von Untersuchungen.<br /><br />- Schaffung eines Studienkabinetts zur Erkennung und Auswertung guter Bildungspraktiken, Fallstudien, Analyse von Gesetzgebung und Bildungspolitiken, zur Formulierung von Initiativen und Entwicklung von Projekten.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2524','0','P_01','<p>El fomento de la investigaci&oacute;n e innovaci&oacute;n educativas es un elemento que favorece la mejora cualitativa de la ense&ntilde;anza y, por ello, el Ministerio de Educaci&oacute;n,&nbsp; a trav&eacute;s del IFIIE, ha venido desarrollando desde hace a&ntilde;os un conjunto de iniciativas encaminadas a promover la realizaci&oacute;n, por parte de los miembros de la comunidad educativa, de actividades investigadoras e innovadoras que contribuyan a renovar la pr&aacute;ctica docente y a satisfacer las necesidades del sistema educativo.<br /><br />En cumplimiento de dicho objetivo y con el fin de contribuir desde el Sistema Educativo a incentivar al profesorado impulsando su implicaci&oacute;n en la investigaci&oacute;n e innovaci&oacute;n educativa, se crearon, mediante Orden ECI/1709/2005 de 21 de mayo, BOE de 9 de junio, los Premios Nacionales de Investigaci&oacute;n e Innovaci&oacute;n Educativa.<br /><br />A&uacute;n cuando la convocatoria est&aacute; abierta a trabajos de muy diferentes tem&aacute;ticas, en los &uacute;ltimos a&ntilde;os han sido objeto de atenci&oacute;n preferente los relacionados con la educaci&oacute;n para la paz, la prevenci&oacute;n de la violencia e intolerancia, la atenci&oacute;n al alumnado inmigrante y la educaci&oacute;n intercultural.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2531','0','Qué es','El Centro de Estudios y Documentaci&oacute;n sobre Inmigraci&oacute;n, Racismo y Xenofobia, MUGAK, impulsado desde SOS Arrazakeria, viene desarrollando su labor desde 1995 y ha recopilado abundante informaci&oacute;n (libros, revistas, documentaci&oacute;n, v&iacute;deos...) que est&aacute; a disposici&oacute;n de cualquier persona. Edita la revista Mugak de periodicidad trimestral y una Revista de Prensa, que se env&iacute;a diaria y gratuitamente a quienes lo solicitan con los contenidos que aparecen en el d&iacute;a a d&iacute;a en m&aacute;s de veinte peri&oacute;dicos del Estado espa&ntilde;ol.&nbsp; Organizan todo tipo de seminarios, conferencias, exposiciones, etc. y dinamizan varios grupos de gente joven que intervienen en tareas de sensibilizaci&oacute;n. <br />Desde 1995 colaboran con Statewatch en el seguimiento de las libertades civiles en Europa. Desde 2003 forman parte de la red europea One line - More Colour in the Media trabajando en torno a la presencia de las minor&iacute;as en los medios. Forman parte del n&uacute;cleo que ha puesto en pie la European Civil Liberties Network. En 2005 participaron en la Asamblea Constitutiva de la Red Migreurop que trabaja en la defensa de los derechos fundamentales de las personas migrantes privadas de libertad o sometidas a las restricciones impuestas a la libre circulaci&oacute;n de personas.',null);
