Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('462','0','Qué es','<div class="info_portada" id="ctl00_ContentPlaceHolder1_pnlInfo"><p>Instituci&oacute;n privada e independiente, sin &aacute;nimo de lucro, que trabaja por la construcci&oacute;n de una sociedad m&aacute;s justa y solidaria.</p><p>A lo largo de su trayectoria, <strong>FUHEM </strong>ha impulsado diversas iniciativas en los &aacute;mbitos de la educaci&oacute;n, la investigaci&oacute;n para la paz, las preocupaciones ecol&oacute;gicas y medioambientales y el desarrollo social en general.</p><p>Con un profundo sentido de servicio c&iacute;vico, <strong>FUHEM </strong>ha desarrollado su labor con el fin de contribuir al debate plural y el fortalecimiento de la vida democr&aacute;tica. Todo ello desde el rigor y la revisi&oacute;n cr&iacute;tica de sus trabajos y proyectos.</p><p>Actualmente, FUHEM centra su actividad social en dos grandes &aacute;reas:</p><ul>    <li><strong>Educaci&oacute;n</strong>: en sus centros escolares, FUHEM desarrolla un proyecto educativo de calidad, basado en la innovaci&oacute;n, el rigor acad&eacute;mico, el desarrollo integral de la persona y la promoci&oacute;n de los valores. </li>    <li><strong>CIP-Ecosocial</strong>: espacio de reflexi&oacute;n y debate social sobre los principales problemas y tendencias de nuestro tiempo, relativos a la sostenibilidad, la cohesi&oacute;n social, la calidad de la democracia y el binomio paz y seguridad. </li></ul></div><div class="contenedor_noticias_portada" id="ctl00_ContentPlaceHolder1_panelNoticias"><div style="PADDING-RIGHT: 10px; PADDING-LEFT: 10px; PADDING-BOTTOM: 10px; PADDING-TOP: 0px"><div class="capaActualidad">&nbsp;</div><div class="ultimasNoticiasItem"><div class="ultimasNoticiasImagen">&nbsp;</div><div id="ctl00_ContentPlaceHolder1_UltimasNoticias1_rptnoticias_ctl00_pnlUltimasNoticiasInfo" style="MARGIN-LEFT: 105px">&nbsp;</div></div><div class="ultimasNoticiasItem"><div class="ultimasNoticiasImagen">&nbsp;</div><div id="ctl00_ContentPlaceHolder1_UltimasNoticias1_rptnoticias_ctl01_pnlUltimasNoticiasInfo" style="MARGIN-LEFT: 105px">&nbsp;</div></div></div></div>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2903','0','II Plan Integral para la Inmigración en Andalucía','Cuatro a&ntilde;os despu&eacute;s del I Plan Integral para la Inmigraci&oacute;n en Andaluc&iacute;a,&nbsp;el II Plan&nbsp;se ha enriquecido de la evaluaci&oacute;n y el contraste con la realidad de las actuaciones planteadas inicialmente. El plan incluye datos descriptivos del fen&oacute;meno migratorio en Andaluc&iacute;a, el marco jur&iacute;dico, el dise&ntilde;o organizativo, los principios rectores, los objetivos generales, las &aacute;reas de intervenci&oacute;n y el modelo de seguimiento y evaluaci&oacute;n.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2904','0','I Plan Integral para la Inmigración en Andalucía','El Plan supone, para las diferentes administraciones p&uacute;blicas, una nueva estrategia de intervenci&oacute;n y planificaci&oacute;n basada en un dise&ntilde;o integrado de la pol&iacute;tica de la Comunidad Andaluza en materia de inmigraci&oacute;n. Este dise&ntilde;o permite abordar el hecho migratorio en todas sus facetas y &aacute;reas, optimizando recursos, de forma sistematizada, bas&aacute;ndose en unos principios rectores y objetivos generales que impregnan todo su contenido.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2905','0','Plan Andaluz de Educación de Inmigrantes','Este Plan nace como una iniciativa derivada de la Ley 9/1999, de 18 de noviembre, de Solidaridad en la Educaci&oacute;n, uno de cuyos objetivos es &quot;potenciar el valor de la interculturalidad, integrando en el hecho educativo la riqueza que supone el conocimiento y respeto por la cultura propia de los grupos minoritarios&quot;.<br />',null);
