Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('851','1',null,'In this section, we''ll be taking a closer look at the key characteristics of other educational systems, especially those from which our students have originated, with the aim of gaining a better understanding of the educational context in their countries of origin. We will also be including useful information on qualification validation, relating to both university and non-university education: current agreements, how to apply for validation, who to contact, etc.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1122','1',null,'This part of the portal provides useful links to relevant information on migratory movements, population evolution, the unique diversity of students attending schools today, etc.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1121','1',null,'The links provided include national and international organisations and institutions, Statistics Offices and Regional Councils from every Autonomous Community. In addition, each of them is accompanied by a suggested route to assist you in your search.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1127','1',null,'As well as offering a list of links, our aim is to configure a network map that integrates all the institutions, organisations and groups involved in diversity on the Internet, offering resources, material, information and/or training. The idea is to provide an open meeting point which is constantly being developed, to which initiatives and projects will be added to help us weave a virtual space on interculturality.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1131','1',null,'The information has been structured into 11 subsections:<br /><br />-Interculturality and education<br />-Migrations<br />-Peace, cooperation and development<br />-Democracy and participation<br />-Human Rights<br />-Gender and diversity<br />-Mediation<br />-Information on solidarity and alternative issues<br />-National and international institutions and organisations<br />-Sources of information on education<br />-Stories and oral narrative<br /><br />If your organisation features Internet content&nbsp;that is similar to those mentioned above and has not yet been included in our map, please get in contact with us.',null);
