Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1506','159',null,'&middot;&nbsp;Ikastetxe eta talde profesionalei aholkularitza eta trebakuntza eskaintzea kultura arteko eskuduntzen garapenerako.<br /><br />&middot;&nbsp;Gogoeta, elkartruke eta proposamenen formulaziorako topaketa eremuak eta lankidetza sareak sustatzea.&nbsp;<br /><br />&middot;&nbsp;Gure ikasleen herrialdeetako hezkuntza sistemei buruzko informazio eskaini, curriculumaren, antolaketaren eta metodologiaren arloan.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1120','159',null,'Gure munduko arlo garrantzitsua da kultura erreferentzia desberdineko jendearen arteko beharrezko lotura eta elkartzea. Agian beti izan da horrela, baina orain eta hemen elkartze horrek ezaugarrie ta larrialdi bereziak ditu. Hezkuntzaren arloan beharrezkoa da <em>hezkuntzaren kontzeptu zabal eta anitza</em> erabiliz lan egitea,&nbsp;<em>hezkuntzaren gertaeraren ikuspegi komunitarioarekin</em>, herri berri, atsegin eta nahitaez kultura artekoa garatzera bideratuta.&nbsp;<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1494','159',null,'Horregatik, CREADEk administrazio eta hezkuntza erakundeek baino instantzia gehiago bildu behar ditu. Azken finean,&nbsp;<em>de facto</em> dagoeneko baterako proiektu batean bilduta dauden guztiak: etorkinen integrazioa, kohesio soziala eta gizartea bere osotasunean&nbsp;heztea. Ezinbestekoa da aldi berean lan egitea horretan gizartea antolatzen duten eremu guztietatik,&nbsp;<em>ikuspegi oso eta diziplina artekoarekin</em>.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2303','159',null,'- Prestakuntza eta aholkularitza ikastetxe eta lan taldeek.<br /><br />- Hezkuntzaren eremuko, eremu sozialeko eta klturaleko intereseko baliabide eta materialen edizioa.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1494','89',null,'A tal fine, il CREADE deve cercare di riunire ulteriori istanze, oltre alle&nbsp;proprie Amministrazioni e Istituzioni educative. In particolare, deve rivolgersi a tutte quelle istanze&nbsp;che di fatto sono gi&agrave; riunite intorno a un progetto comune: l''integrazione degli immigranti, la coesione sociale e l''educazione cittadina della societ&agrave; nel suo insieme. Per perseguire questo proposito, &egrave; imprescindibile che, da&nbsp;tutti&nbsp;i settori&nbsp;che strutturano la societ&agrave;, si lavori insieme, con&nbsp;un <em>approccio integrale e multidisciplinare</em><em>.</em>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1493','89',null,'Non possiamo intraprendere questa ambiziosa&nbsp;impresa isolatamente. Il sistema educativo da solo non pu&ograve; farcela e senza la sua collaborazione, neppure le politiche sociali.<br /><br />Le attuazioni da realizzare in&nbsp;ciascuno degli&nbsp;scenari interessati sono di natura molto diversa, ma &egrave; ora di <em>coordinare gli sforzi, condividere le&nbsp;informazioni, diffondere buone pratiche e creare delle reti di lavoro congiunto&nbsp;</em>con le&nbsp;amministrazioni educative, gli agenti sociali e tutti i settori implicati nella <em>costruzione di una cittadinanza interculturale, </em>che deve essere, come abbiamo gi&agrave; sottolineato, il nostro ultimo e grande obiettivo.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1506','147',null,'<p>&middot;&nbsp; Oferir a centres i equips de professionals assessorament i formaci&oacute; per al desenvolupament de compet&egrave;ncies interculturals.<br /><br />&middot;&nbsp; Promoure xarxes de col&middot;laboraci&oacute; i espais de trobada per a la reflexi&oacute;, l''intercambi i la formulacio de propostes.<br /><br />&middot;&nbsp; Facilitar informaci&oacute; sobre els sistemes educatius dels pa&iuml;ssos d''origen del nostre alumnat, en els seus aspectes curriculars, organitzatius i metodol&ograve;gics.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2303','112',null,'- Formaci&oacute;n e asesoramento a centros e equipos de traballo.<br /><br />- Edici&oacute;n de materiais e recursos de interese para o &aacute;mbito educativo, social e cultural.<br />',null);
