Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2518','159',null,'Herri berri horietarako bidea kultura arteko ikuspegia pixkanaka hartzetik pasatzen da. Kultura arteko hezkuntza aldi berean aldaketa prozesu eta lortu beharreko muga gisa definitzen da. CREADE apustu ausart eta konprometitu gisa sortu da, prozesu hori amaierara bideratzeko asmoz.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1495','159',null,'Asmo nagusienen artean dago <em>nahi dugun gizarte eta eskola ereduaren inguruan gogoeta sakona egitea</em>,&nbsp;eta gizarte eta heziketa mailako&nbsp;eraldaketa artikulatzen&nbsp;hasi, arreta&nbsp;ikasle batzuei eskaintzeari utzi eta, pixkanaka, guztiengana zuzentzeko... Gizarte zuzenagoa, berdintasunezkoagoa,&nbsp;hartzaileagoa eta kultura artekoagoa, zalantzarik gabe, kolektiboaren zeregina baita. Herritar berriak eraikitzea da kontua, guztiok eta bakoitzak protagonista izateko aukera izanik.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1493','159',null,'<p>Ezin dugu lan egin modu isolatuan zeregin honetan. Hezkuntza sistemak, berez, ezin du eta politika sozialek ere ez hezkuntza sistemarik gabe.</p><p>Inplikatutako&nbsp;agertoki bakoitzetik egindako ekintzak abian jarri behar dira; izaera oso desberdinekoak dira baina&nbsp;<em>esfortzuak koordinatzeko, informazioa partekatzeko, jardute onak zabaltzeko eta baterako lan sareak sortzeko </em>ordua da hezkuntza administrazioetatik, eragile sozialetatik eta&nbsp;<em>kultura artekotasuneko herritarrak egiteko zereginean</em> inplikatutako sektore guztietatik. Esan dugun gisan, horrek izan behar du azken helburua eta nagusia. </p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2304','159',null,'<p>Kultura artekotasunaren inguruan daugoen informazioa eta baliabideak bildu eta sistematizatzearekin batera, eta ataria mantentzea eta eguneratzearekin batera, CREADEk beste ekintza ildo garrantzitsu batzuk ere garatu nahi ditu:</p><p>- Kultura arteko hezkuntzaren gaineko ikerketa ildoa mantentzea sarien deialdiak eginez eta ikerketak garatuz.<br /><br />- Hezkuntza portaera onen analisia eta antzematea egiteko Ikerketa kabinetea sortzea, kasuak aztertzeko, legedia eta hezkuntza politikak aztertzeko, ekimenak formulatzeko eta proiektuak garatzeko.<br /></p>',null);
