Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2304','147',null,'Juntament amb&nbsp;el treball&nbsp;de recollida i sistematitzaci&oacute; de la informaci&oacute; i els recursos existents al voltant de l''educaci&oacute; intercultural, i el manteniment i actualitzaci&oacute; del portal, el CREADE t&eacute; com a prop&ograve;sit desenvolupar unes altres l&iacute;nies d''actuaci&oacute; importants:<br /><br />- Manteniment de la l&iacute;nia de recerca sobre educaci&oacute; intercultural per mitj&agrave; de la convocat&ograve;ria de premis i el desenvolupament de recerques.<br /><br />- Creaci&oacute; d''un gabinet d''Estudis per a la detecci&oacute; i an&agrave;lisi de bones pr&agrave;ctiques educatives, l''estudi de casos, l''an&agrave;lisi de la legislaci&oacute; i les pol&iacute;tiques educatives, la formulaci&oacute; d''iniciatives i el desenvolupament de projectes.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2303','147',null,'<br />- Formaci&oacute; i assessorament de centres i equips de treball.<br /><br />- Edici&oacute; de materials i recursos d''inter&egrave;s per a l''&agrave;mbit educatiu, social i cultural.',null);
