Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2874','112','Que é','<strong>Coa intenci&oacute;n de desenvolver pol&iacute;ticas de integraci&oacute;n dos inmigrantes, o Goberno creou, dependendo da Secretar&iacute;a de Estado de Inmigraci&oacute;n e Emigraci&oacute;n, a Direcci&oacute;n Xeral de Integraci&oacute;n dos Inmigrantes, &aacute; que se lle asignan competencias en materia de promoci&oacute;n de pol&iacute;ticas de igualdade de trato e non-discriminaci&oacute;n pola orixe racial ou &eacute;tnica. No marco destas competencias, creouse o Observatorio Espa&ntilde;ol do Racismo e a Xenofobia, con funci&oacute;ns de estudo e an&aacute;lise, e capacidade de elevar propostas de actuaci&oacute;n, en materia de loita contra o racismo e a xenofobia.</strong><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><meta name="ProgId" content="Word.Document" /><meta name="Generator" content="Microsoft Word 11" /><meta name="Originator" content="Microsoft Word 11" /><link rel="File-List" href="file:///C:\DOCUME~1\CRISTI~1\CONFIG~1\Temp\msohtml1\01\clip_filelist.xml" /><o:smarttagtype namespaceuri="urn:schemas-microsoft-com:office:smarttags" name="PersonNameProductID"></o:smarttagtype><!--[if gte mso 9]><xml><w:WordDocument><w:View>Normal</w:View><w:Zoom>0</w:Zoom><w:HyphenationZone>21</w:HyphenationZone><w:PunctuationKerning /><w:ValidateAgainstSchemas /><w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid><w:IgnoreMixedContent>false</w:IgnoreMixedContent><w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText><w:Compatibility><w:BreakWrappedTables /><w:SnapToGridInCell /><w:WrapTextWithPunct /><w:UseAsianBreakRules /><w:DontGrowAutofit /></w:Compatibility></w:WordDocument></xml><![endif]--><!--[if gte mso 9]><xml><w:LatentStyles DefLockedState="false" LatentStyleCount="156"></w:LatentStyles></xml><![endif]--><!--[if !mso]><objectclassid="clsid:38481807-CA0E-42D2-BF39-B33AF135CC4D" id=ieooui></object><style>st1\:*{behavior:url(#ie