Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1507','147',null,'<p>&middot;&nbsp; Proporcionar recursos interculturals que donin resposta a les demandes dels&nbsp;i les professionals de l''&agrave;mbit social i educatiu. </p><p>&middot;&nbsp; Obtenir, sistematizar i generar tota mena d''informaci&oacute; relacionada amb l''educaci&oacute; intercultural. </p><p>&middot;&nbsp; Desenvolupar nous materials i eines en aquelles &agrave;rees i tem&agrave;tiques en les quals es percebin necessitats </p><p>&middot;&nbsp; Fomentar innovacions i recerques en l''&agrave;mbit de l''atenci&oacute; educativa a la diversitat cultural.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2304','112',null,'<p>Xunto ao labor de recollida e sistematizaci&oacute;n da informaci&oacute;n e os recursos existentes en torno &aacute; educaci&oacute;n intercultural, e o mantemento e actualizaci&oacute;n do portal, o CREADE&nbsp; proponse desenvolver&nbsp;outras&nbsp;li&ntilde;as de actuaci&oacute;n importantes:</p><p>- Mantemento&nbsp;da li&ntilde;a de investigaci&oacute;n sobre educaci&oacute;n intercultural a trav&eacute;s da convocatoria de premios e o desenvolvemento de investigaci&oacute;ns.<br /><br />- Creaci&oacute;n dun Gabinete de Estudos para a detecci&oacute;n e an&aacute;lise de boas pr&aacute;cticas educativas, o estudo de casos, a an&aacute;lise da lexislaci&oacute;n e&nbsp;as pol&iacute;ticas educativas, a formulaci&oacute;n de iniciativas e o desenvolvemento de proxectos.<br /><br /></p>',null);
