Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2304','17',null,'<span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO"><p style="BACKGROUND: white"><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">Pe l&acirc;ng&#259; activitatea de a culege &#351;i sistematiza informa&#355;ia &#351;i resursele existente &icirc;n ce prive&#351;te educa&#355;ia intercultural&#259;, pe l&acirc;ng&#259; men&#355;inerea &#351;i actualizarea portalului, CREADE &icirc;&#351;i mai propune s&#259; dezvolte &#351;i alte linii de activitate importante:<p><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">Men&#355;inerea liniei de cercetare &icirc;n domeniul educa&#355;iei interculturale prin intermediul premiilor &#351;i dezvolt&#259;rii cercet&#259;rii.<p><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">Crearea unui Cabinet de Studii cu scopul de a identifica &#351;i analiza practicile educa&#355;ionale care au dat rezultate bune, de a face studii de caz, de a analiza legisla&#355;ia &#351;i politicile educative, de a formula ini&#355;iative &#351;i de a dezvolta proiecte. </span></p></span></p></span></p></span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2303','17',null,'<p class="MsoNormal" style="BACKGROUND: white; MARGIN: 0cm 0cm 0pt"><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">Servicii de Formare &#351;i consultan&#355;&#259; pentru centre &#351;i echipele de lucru.<p><span lang="RO" style="FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-ansi-language: RO">Publicarea&nbsp;de materiale &#351;i resurse de interes pentru domeniul educativ, social &#351;i cultural.<p>&nbsp;</p></span></p></span></p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1507','1',null,'<p style="BACKGROUND: white; MARGIN-BOTTOM: 12pt"><span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">To provide intercultural resources which address the demands of professionals working in the social and educational fields.<br />&middot;&nbsp;To gather, process and generate all kinds of information relating to intercultural education.<br />&middot;&nbsp;To develop new materials and tools in the areas and subjects where needs have been identified.<br />&middot;&nbsp;To encourage innovation and research in the field of attention to cultural diversity in education.<br style="mso-special-character: line-break" /><br style="mso-special-character: line-break" /><p>&nbsp;</p></span></p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1506','1',null,'<p style="BACKGROUND: white; MARGIN-BOTTOM: 12pt"><span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">&middot;&nbsp;To provide educational centres and teams of professionals with advice and training for them to develop intercultural skills.</span><span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"><br />&middot;&nbsp;To promote collaboration networks and meeting places for reflection, exchanging experiences and developing proposals.&nbsp;<br />&middot;&nbsp;To provide information on the educational systems in the countries of origin of our students, including their curricula, organisational structures and methodologies.<p>&nbsp;</p></span></p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1120','1',null,'An important aspect of our world today is the convergence and necessary interconnection between people with different cultural backgrounds. Perhaps this has always been the case, but here and now this convergence has taken on specific characteristics and imperatives. In the field of education there is a need to work&nbsp;<span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: EN-GB; mso-fareast-language: ES; mso-bidi-language: AR-SA"><em>based on a broad-ranging and plural educational concept</em></span> with a <span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: EN-GB; mso-fareast-language: ES; mso-bidi-language: AR-SA"><em>community-based approach to education</em></span><span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: EN-GB; mso-fareast-language: ES; mso-bidi-language: AR-SA"> </span>geared towards developing a new kind of citizenship which is both ideally and by necessity&nbsp;intercultural.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2569','0','Qué es','e-migraATEI tiene como objetivo ofrecer una respuesta eficaz a la necesidad de informaci&oacute;n sobre las posibilidades formativas, culturales y de comunicaci&oacute;n que brinda el pa&iacute;s de acogida a todas aquellas comunidades de emigrantes que proceden o tienen como destino cualquier pa&iacute;s iberoamericano.<br /><br /><br />',null);
