Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2909','0','Servicio de Traducción para uso de los Centros Docentes','La Consejer&iacute;a de Educaci&oacute;n ha puesto en marcha un Servicio de Traducci&oacute;n de documentos para la mediaci&oacute;n intercultural, para uso de los centros educativos, con el fin de configurar una nueva herramienta en la atenci&oacute;n educativa del Alumnado Inmigrante escolarizado en Andaluc&iacute;a y de sus familias.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2908','0','Plan andaluz de Educación para la Cultura de Paz y la No Violencia','Plan que se suma a las iniciativas de Naciones Unidas en el marco del Decenio de Cultura de Paz y Noviolencia (2001 - 2010). Su elaboraci&oacute;n es resultado de la consulta de las acciones realizadas en este campo tanto por el profesorado y diversos agentes educativos y sociales como por expertos universitarios y centros de investigaci&oacute;n para la paz.<br />',null);
