Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2926','0','¿Qué es el CREI?','<p>Es un centro de <strong>car&aacute;cter regional</strong> que aporta apoyo t&eacute;cnico y did&aacute;ctico a la comunidad educativa sobre la atenci&oacute;n al alumnado extranjero y de minor&iacute;as.<br />Depende funcionalmente del Servicio de Atenci&oacute;n a la Diversidad de la Direcci&oacute;n General de Planificaci&oacute;n, Ordenaci&oacute;n e Inspecci&oacute;n Educativa de la Consejer&iacute;a de Educaci&oacute;n.</p>',null);
