Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2928','0','¿Qué es?','Es un centro creado por el Departamento de Educaci&oacute;n, Cultura y Deporte del Gobierno de Arag&oacute;n al servicio de la comunidad educativa aragonesa para todos los temas relacionados con la acogida e integraci&oacute;n de la poblaci&oacute;n de origen inmigrante, en el contexto educativo desde la &oacute;ptica de la interculturalidad.',null);
