Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2932','0','Aula Vitual de Español: A.V.E.','La Consejer&iacute;a de Educaci&oacute;n tiene suscrito un convenio de colaboraci&oacute;n con el Instituto Cervantes para la utilizaci&oacute;n del Aula Virtual de Espa&ntilde;ol (A.V.E.), con objeto de facilitar el aprendizaje del espa&ntilde;ol al alumnado inmigrante a trav&eacute;s de la modalidad de formaci&oacute;n a distancia, mediante cursos organizados en diferentes niveles, bajo la supervisi&oacute;n de un tutor o tutora del centro.',null);
