Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('743','89',null,'Questa pagina web usa Unicode UTF-8. Se in qualche sezione compaiono caratteri strani, probabilmente si dovr&agrave;&nbsp;impostare il computer per visualizzarli correttamente. Pu&ograve; essere richiesta l''installazione di quache fonte.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2304','1',null,'<p style="BACKGROUND: white"><span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Along with its work collating and processing information and existing resources on intercultural information and maintaining and updating its portal, CREADE proposes to develop other important lines of action:&nbsp;</span></p><p>&nbsp;</p><p style="BACKGROUND: white; MARGIN-BOTTOM: 12pt"><span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">- Supporting the line of research on intercultural education through the organisation of prizes and grants for research.<br /><br />- Creating a Study Committee to identify and analyse good educational practices, carry out case studies, analyse legislation and educational policies, formulate initiatives and develop projects.<p>&nbsp;</p></span></p><p>&nbsp;</p><p class="MsoNormal" style="BACKGROUND: white; MARGIN: 0cm 0cm 0pt"><span lang="EN-GB" style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>',null);
