Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('9','182',null,'<p style="text-align: justify;" class="MsoNormal"><span lang="DE" style="font-size: 13pt; font-family: Arial; color: black;">Dieser Abschnitt ist als Datenbank f&uuml;r n&uuml;tzliche Informationen und Ressourcen gedacht, um die neuen Technologien in den Alltag zu integrieren.<o:p></o:p></span></p><p style="text-align: justify;" class="MsoNormal"><span lang="DE" style="font-size: 13pt; font-family: Arial; color: black;">Neben interessanten Referenzen und Links bietet CREADETIC interaktive Ressourcen und Materialien f&uuml;r formelle und informelle Unterrichtssituationen, Tools zur Entwicklung eigener Anwendungen und eine gro&szlig;e Auswahl an audiovisuellen Materialien (statische und bewegliche Bilder, Dokumentarfilme, Aufnahmen, etc.).<o:p></o:p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2258','182',null,'<p style="text-align: justify;" class="MsoNormal"><span lang="DE" style="font-family: Arial; color: black;">Alle hier verf&uuml;gbaren Ressourcen, sowohl jene, die von CREADE erstellt wurden wie auch jene, auf die verwiesen wird, d&uuml;rfen auf jedem Datentr&auml;ger benutzt werden. Daf&uuml;r ist keine Genehmigung n&ouml;tig, es muss nur verpflichtend die Quelle angegeben werden.<o:p></o:p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1127','182',null,'<p style="text-align: justify;"><span lang="DE" style="">Dieser Abschnitt ist nicht als reine Liste aus Links gedacht, sondern versucht, ein ganzes Netz aus Links zu Institutionen, Organisationen und Gruppen zu sein, die sich im Internet mit Diversit&auml;t besch&auml;ftigen und Ressourcen, Materialien, Informationen und/oder Kurse anbieten. Es soll ein Treffpunkt sein, der einem st&auml;ndigen Ver&auml;nderungsprozess unterliegt und f&uuml;r Initiativen und Vorschl&auml;ge offen ist, die uns helfen, einen virtuellen Raum f&uuml;r Interkulturalit&auml;t zu schaffen.</span><span lang="DE" style="font-size: 13pt; font-family: Arial; color: black;"><o:p></o:p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1131','182',null,'Die Informationen wurden in 11 Abschnitte untergliedert:<br /><br />- Interkulturalit&auml;t und Erziehung<br />- Migrationen<br />- Frieden, Kooperation und Entwicklung<br />- Demokratie und Teilnahme<br />- Menschenrehte<br />&nbsp;- Geschlecht und Diversit&auml;t<br />&nbsp;- Mediation<br />&nbsp;- Solidarische und alternative Informationen<br />&nbsp;- Internationale und landesweite Institutionen und Organisationen<br />&nbsp;- Informationsquellen im Bildungsbereich<br />&nbsp;- Geschichten und m&uuml;ndliche Erz&auml;hlungen',null);
